package com.github.meteoorkip.utils;


import org.junit.jupiter.api.Test;

import java.io.IOException;

import static org.junit.jupiter.api.Assertions.assertEquals;


public final class FileUtilsTest {

    @Test
    public void testBase64() throws IOException {
        String res = FileUtils.ImageToBase64(FileUtils.fromResources("images/testimage.jpg"));
        assertEquals("", res);
    }

    @Test
    public void testReadStringFromFile() throws IOException {
        assertEquals(FileUtils.readFromFile(FileUtils.fromResources("library/simple_graphs/graph1.dot")).replaceAll("[\n\r]", ""),
                "//A simple undirected graph"+
                "graph graphname {" +
                "    a -- b -- c;" +
                "    b -- d;" +
                "}");
    }
}
